package com.retailstore.discountservice.controller;import com.retailstore.discountservice.request.BillRequest;import com.retailstore.discountservice.model.Discount;import com.retailstore.discountservice.service.DiscountService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/discount")@Slf4jpublic class DiscountController {    private final DiscountService discountService;    @Autowired    public DiscountController(DiscountService discountService) {        this.discountService = discountService;    }    @PostMapping("/calculate")    public Double calculateDiscount(@RequestBody BillRequest billRequest) {        log.info("Inside calculateDiscount method of DiscountController");        return discountService.calculateDiscount(billRequest);    }    @PostMapping    public Discount createDiscount(@RequestBody Discount discount) {        log.info("Inside add method of DiscountController");        return discountService.createDiscount(discount);    }    @PutMapping    public Discount updateDiscount(@RequestBody Discount discount) {        log.info("Inside update method of DiscountController");        return discountService.updateDiscount(discount);    }    @DeleteMapping    public void deleteDiscount(@RequestBody Discount discount) {        log.info("Inside delete method of DiscountController");        discountService.deleteDiscount(discount);    }    @GetMapping    public Iterable<Discount> getAllDiscounts() {        log.info("Inside getAll method of DiscountController");        return discountService.getAllDiscounts();    }    @GetMapping("/{id}")    public Discount getDiscount(@PathVariable Long id) {        log.info("Inside get method of DiscountController");        return discountService.getDiscount(id);    }}