package com.retailstore.discountservice.controller;import com.retailstore.discountservice.model.Item;import com.retailstore.discountservice.service.ItemService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/item")@Slf4jpublic class ItemController {    private final ItemService itemService;    @Autowired    public ItemController(ItemService itemService) {        this.itemService = itemService;    }    @PostMapping    public Item createItem(@RequestBody Item item) {        log.info("Inside add method of ItemController");        return itemService.createItem(item);    }    @PutMapping    public Item updateItem(@RequestBody Item item) {        log.info("Inside update method of ItemController");        return itemService.updateItem(item);    }    @DeleteMapping    public void deleteItem(@RequestBody Item item) {        log.info("Inside delete method of ItemController");        itemService.deleteItem(item);    }    @GetMapping    public Iterable<Item> getAllItems() {        log.info("Inside getAll method of ItemController");        return itemService.getAllItems();    }    @GetMapping("/{id}")    public Item getItem(@PathVariable Long id) {        log.info("Inside get method of ItemController");        return itemService.getItem(id);    }}