package com.retailstore.discountservice.controller;import com.retailstore.discountservice.model.User;import com.retailstore.discountservice.service.UserService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/user")@Slf4jpublic class UserController {    private final UserService userService;    @Autowired    public UserController(UserService userService) {        this.userService = userService;    }    @PostMapping    public User createUser(@RequestBody User user) {        log.info("Inside add method of UserController");        return userService.createUser(user);    }    @PutMapping    public User updateUser(@RequestBody User user) {        log.info("Inside update method of UserController");        return userService.updateUser(user);    }    @DeleteMapping    public void deleteUser(@RequestBody User user) {        log.info("Inside delete method of UserController");        userService.deleteUser(user);    }    @GetMapping    public Iterable<User> getAllUsers() {        log.info("Inside getAll method of UserController");        return userService.getAllUsers();    }    @GetMapping("/{id}")    public User getUser(@PathVariable Long id) {        log.info("Inside get method of UserController");        return userService.getUser(id);    }}