package com.retailstore.discountservice.exception;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestControllerAdvice;@RestControllerAdvicepublic class DiscountExceptionHandler {    @ExceptionHandler(value = NullValueFoundException.class)    public ResponseEntity<String> handleNoItemsFoundExceptionException(NullValueFoundException ex) {        return new ResponseEntity<>(ex.getMessage(), HttpStatus.BAD_REQUEST);    }}