package com.retailstore.discountservice.model;import com.retailstore.discountservice.constant.UserType;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Discount {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String discountName;    private String discountDescription;    private Double discountPercentage;    private Double discountValue;    private Double minimumBillAmount;    private Boolean isPercentageBasedDiscount;    @Column(nullable = false)    private UserType discountUserType;}