package com.retailstore.discountservice.model.impl;import com.retailstore.discountservice.model.BaseDiscount;import com.retailstore.discountservice.model.Discount;public class BillAmountBasedDiscount implements BaseDiscount {    @Override    public Double applyDiscount(Double amount, Discount discount) {        return (amount / discount.getMinimumBillAmount()) * discount.getDiscountValue();    }}